package Kernel::System::HelloWorld;

use strict;
use warnings;

# itt sorolja fel az objektum függőségeit (például Kernel::System::DB)
our @ObjectDependencies = (
    # 'Kernel::System::DB',
);

sub new {
    my ( $Type, %Param ) = @_;

    # kivonat lefoglalása az új objektumhoz
    my $Self = {};
    bless ($Self, $Type);

    return $Self;
}

sub GetHelloWorldText {
    my ( $Self, %Param ) = @_;

    # a DBObject lekérése a központi objektumkezelőtől
    # my $DBObject = $Kernel::OM->Get('Kernel::System::DB');

    return 'Hello World!';
}

1;

package Kernel::Language::hu_AgentHelloWorld;

use strict;
use warnings;

sub Data {
    my $Self = shift;

    $Self->{Translation}->{'Hello World!'} = 'Helló, Világ!';

    return 1;
}
1;
